package net.bcsoft.shape;import static java.lang.Math.PI;public class Cerchio {    private int raggio;    public double calculateArea(int raggio) {        double area = Math.PI * (raggio * 2);        return area;    }    public double calculatePerimeter(int raggio) {        double perimeter = 2 * Math.PI * raggio;        return perimeter;    }    public void setRaggio(int raggio) {        this.raggio = raggio;    }    public int getRaggio() {        return raggio;    }}